              <SelectContent>
                <SelectItem value="none">Keine Auswahl</SelectItem>
                {werkzeuge.map(w => <SelectItem key={w.record_id} value={w.record_id}>{w.fields.werkzeugname || w.record_id}</SelectItem>)}
              </SelectContent>
            </Select>
